import { Container, Grid , Paper, Button, Typography} from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import {useDispatch,useSelector} from 'react-redux';
import { createApiStructure } from '../../api';

import Input from './Input';
import useStyles from './styles';
import { API_SUBMIT } from '../../constants/actionTypes';
import currentId from '../../reducers/currentId';


const initialState = {apiName:'', apiCode:'',apiStructureText:''};

const ApiStructure = ()=>{
    const classes = useStyles();
    const [form, setForm ] = useState(initialState);
    const dispatch = useDispatch();
    const currentId = useSelector((state) => (state.currentId));

    const handleChange =(e)=>{
        setForm({...form,[e.target.name]:e.target.value});
    }
    const handleSubmit=(e)=>{        
        e.preventDefault();
        dispatch( {type: API_SUBMIT, data: form} );
    }
    const loadCurrentIdData = ()=>{
        const apiStructure = useSelector((state) => (currentId ? state.apiStructure.find((message) => message._id === currentId) : null));
        setForm();
    }
    useEffect((currentId)=>{
        loadCurrentIdData();
    },[currentId])

    return (

        <Container component="main" maxWidth="xs">
            <Paper className ={classes.paper} elevation ={3}>
            <Typography component="h1" variant="h5">Add API Structure </Typography>
            <form className={classes.form} onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Input name="apiName" label = "API Name" handleChange={handleChange} autoFocus />
                    <Input name="apiCode" label = "API Code" handleChange={handleChange} autoFocus />
                    <Input name="apiStructureText" label = "API Structure" handleChange={handleChange} type="multiline" multilineNumber="3"/>
                </Grid>
                <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
                    Submit
                </Button>
            </form>

            </Paper>
        </Container>

    );

}

export default ApiStructure
