import { UPDATE_CURRENT_ID } from '../constants/actionTypes';
import * as api from '../api/index.js';

export const updateCurrentId = (currentId) => (dispatch) => {
  try {
    dispatch({ type: UPDATE_CURRENT_ID, payload: currentId });
  } catch (error) {
    console.log(error);
  }
};
