import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core/';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import ThumbUpAltOutlined from '@material-ui/icons/ThumbUpAltOutlined';
import { useDispatch } from 'react-redux';
import moment from 'moment';

import { likePost, deletePost } from '../../../actions/posts';

const ApiRecord = ({ apiRecord, setCurrentId }) => {
  const dispatch = useDispatch();
  const user = JSON.parse(localStorage.getItem('profile'));

  return (
    <Card>
        <Typography variant="h6">{apiRecord.apiName}</Typography>
        <Typography variant="body2">{moment(apiRecord.createdAt).fromNow()}</Typography>
        {(user?.result?.googleId === apiRecord?.creator || user?.result?._id === apiRecord?.creator) && 
        <>
        (
        <div>
          <Button onClick={() => setCurrentId(apiRecord._id)} size="small">
            <MoreHorizIcon fontSize="default" />
          </Button>
        </div>
        <Typography cgutterBottom variant="h5" component="h2">{apiRecord.apiCode}</Typography>
        )</>
        }
        <Typography variant="body2" color="textSecondary" component="p">{apiRecord.apiStructureText}</Typography>

    </Card>
  );
};

export default ApiRecord;
