import { API_SUBMIT ,API_FETCH_ALL} from '../constants/actionTypes';

const  apiStructure = (apiListing = [], action) => {
  switch (action.type) {
    case API_SUBMIT:
      return [...apiListing, action.payload];
    case API_FETCH_ALL:
      return action.payload;
    default:
      return apiListing;
  }
};

export default apiStructure;
