import React, { useEffect, useState } from 'react';
import { Container, Grid , Paper, Button, Typography,TextField} from '@material-ui/core';
import {useDispatch,useSelector} from 'react-redux';

import Input from './Input';
import useStyles from './styles';
import {createApiStructure } from '../../actions/apiStructure';

const initialState = { apiName:'', apiCode:'',apiStructureText:''};

const ApiStructure = ()=>{
    const classes = useStyles();
    const [form, setForm ] = useState(initialState);
    const dispatch = useDispatch();
    const currentId = useSelector((state) => (state.currentId));
//    console.log('currentId after use selector, ', currentId);
    console.log('what is in the state: ',useSelector((state) => (state)));
    const apiRecord = useSelector((state) => (currentId ? state.apiStructure.find((message) => message._id === currentId) : null));
    console.log('apiRecord based on currentId after use selector, ', apiRecord);

    const handleChange =(e)=>{
        setForm({...form,[e.target.name]:e.target.value});
    }
    const handleSubmit=(e)=>{      
        e.preventDefault();
        dispatch( createApiStructure(form) );
    }

    useEffect(()=>{
        console.log('is apiRecord data updated: ', apiRecord);
        setForm({ apiRecord});
        console.log('is form data updated: ', form);
    },[currentId])

    return (
        <Container component="main" maxWidth="xs">
            <Paper className ={classes.paper} elevation ={3}>
            <Typography component="h1" variant="h5">Add API Structure </Typography>
            <form className={classes.form} onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <TextField name="apiName" variant="outlined" label="API Name" fullWidth value={form.apiName} onChange={(e) => setForm({ ...form, title: e.target.value })} />
                    <TextField name="apiCode" label="API Code" fullWidth value={form.apiCode} onChange={(e) => setForm({ ...form, title: e.target.value })} />
                    <TextField name="apiStructureText" label="API Structure" fullWidth value={form.apiStructureText} onChange={(e) => setForm({ ...form, title: e.target.value })} />
                </Grid>
                <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
                    Submit
                </Button>
            </form>

            </Paper>
        </Container>
    );
}

export default ApiStructure
